insert into t_asset_info(partnerNo, releaseDate, releaseFlg, assetName, assetDetails, assetPic, addTimestamp, addUserKey, updTimestamp, updUserKey, updPgmId) values('PA20160620000001', '2016-06-20 00:00:00', '1', '主动管理型产品', '主动管理型产品是鼎锋投资的旗舰产品。以基金专户，契约集合等方式募集客户的资金投资于资本市场，通过Alpha、中短期的统计配对交易及期权多策略叠加，投资于流动性较好的股票和债券等投资标的。该产品具有中低风险、中高收益的特点。在2015年的股灾期间，6月15日到7月6日， 子午管理产品保持无单日回撤的记录。在过去三年中，公司产品实现了15%-25%的年平均回报。<br>此类产品通常每半年或每年度可申购和赎回。', '', '2016-06-20 00:00:00', 'admin', '2016-06-20 00:00:00', 'admin', 'basic');
insert into t_asset_info(partnerNo, releaseDate, releaseFlg, assetName, assetDetails, assetPic, addTimestamp, addUserKey, updTimestamp, updUserKey, updPgmId) values('PA20160620000002', '2016-06-20 00:00:00', '1', '量化对冲产品', '子午量化投资团队经过多年的积累，采用多因子及事件驱动模型自下而上进行个股选择，运用行业及个股量化模型，选择在当前市场情景下有大概率领先标的指数的股票，以获取alpha收益；最后利用股指期货进行套期保值，以降低投资组合风险，并对alpha股票组合进行中短期统计配对交易，取得平稳收益曲线。<br>目前子午已有多只量化产品问世，均表现稳定，收益良好。', '', '2016-06-20 00:00:00', 'admin', '2016-06-20 00:00:00', 'admin', 'basic');
insert into t_asset_info(partnerNo, releaseDate, releaseFlg, assetName, assetDetails, assetPic, addTimestamp, addUserKey, updTimestamp, updUserKey, updPgmId) values('PA20160620000003', '2016-06-20 00:00:00', '1', '股票期权产品', '子午股票期权基金是以场内期权及场外个股期权为主要投资对象的投资基金， 具有较复杂的投资结构， 高杠杆高收益高波动等特点。策略包括：单一期权策略、备兑开仓 （备兑认购期权组合，保护性认沽期权组合）、合成股票套利（通过期权合约合成股票从而与现货套利）、价差组合(认购牛市价差、认购熊市价差、防守型价差 等)、波动率策略 （宽跨式期权组合、蝶式组合等）、及带式期权。<br>此产品通常针对特定客户特定需求而定制交易架构和投资方案。', '', '2016-06-20 00:00:00', 'admin', '2016-06-20 00:00:00', 'admin', 'basic');
